{"code":"import pandas as pd
import json
from pathlib import Path

excel_path = Path('/workspace/dumps/workspace/Cinema_Culture_Grade_Summary.xlsx')
wb = pd.ExcelFile(excel_path)
info = {"sheets": wb.sheet_names}

data = {}
for sheet in wb.sheet_names:
    try:
        df = wb.parse(sheet)
        # Normalize column names
        df.columns = [str(c).strip() for c in df.columns]
        # Convert to records (but limit long text)
        recs = df.to_dict(orient='records')
        data[sheet] = recs
    except Exception as e:
        data[sheet] = {"error": str(e)}

print(json.dumps({"info": info, "data": data}, ensure_ascii=False))","filename":"read_grade_summary.py","timeout":120}
