    },
    {
      "name": "盐",
      "quantity": null,
      "unit": null,
      "text_quantity": "- 盐",
      "notes": "量未指定"
    },
    {
      "name": "糖（可选）",
      "quantity": null,
      "unit": null,
      "text_quantity": "- 糖（可选）",
      "notes": "量未指定"
    },
    {
      "name": "葱花（可选）",
      "quantity": null,
      "unit": null,
      "text_quantity": "- 葱花（可选）",
      "notes": "量未指定"
    },
    {
      "name": "西红柿 =",
      "quantity": null,
      "unit": null,
      "text_quantity": "- 西红柿 = 1 个（约 180g） * 份数",
      "notes": "量未指定"
    },
    {
      "name": "鸡蛋 =",
      "quantity": null,
      "unit": null,
      "text_quantity": "- 鸡蛋 = 1.5 个 * 份数，向上取整",
      "notes": "量未指定"
    },
    {
      "name": "食用油 =",
      "quantity": null,
      "unit": null,
      "text_quantity": "- 食用油 = 4ml * 鸡蛋/个",
      "notes": "量未指定"
    },
    {
      "name": "盐 =",
      "quantity": null,
      "unit": null,
      "text_quantity": "- 盐 = 1.5-2g * 份数",
      "notes": "量未指定"
    },
    {
      "name": "糖 =",
      "quantity": null,
      "unit": null,
      "text_quantity": "- 糖 = 0-2g * 份数",
      "notes": "量未指定"
    },
    {
      "name": "葱花 =",
      "quantity": null,
      "unit": null,
      "text_quantity": "- 葱花 = 0-10g * 份数",
      "notes": "量未指定"
    }
  ],
  "steps": [
    {
      "step": 1,
      "description": "西红柿洗净"
    },
    {
      "step": 2,
      "description": "可选：去掉西红柿的外表皮"
    },
    {
      "step": 3,
      "description": "西红柿去蒂，切成边长不超过 4cm 的小块，即为 `西红柿块`"
    },
    {
      "step": 4,
      "description": "将鸡蛋打入碗中，加入 `1g * 份数` 的盐，搅匀，即为 `鸡蛋液`"
    },
    {
      "step": 5,
      "description": "热锅，加入食用油"
    },
    {
      "step": 6,
      "description": "油热后，倒入 `鸡蛋液`。翻炒至鸡蛋结为固体且颜色微微发黄，即为 `半熟鸡蛋`"
    },
    {
      "step": 7,
      "description": "关火。将 `半熟鸡蛋` 盛盘，重新开火"
    },
    {
      "step": 8,
      "description": "加入 `西红柿块`，锅铲拍打并翻炒 20 秒，或至西红柿软烂"
    },
    {
      "step": 9,
      "description": "向锅中加入 `半熟鸡蛋`，翻炒均匀"
    },
    {
      "step": 10,
      "description": "加入剩余的盐、糖（可选，如果倾向于甜味版本）、葱花（可选），翻炒均匀"
    },
    {
      "step": 11,
      "description": "关火，盛盘"
    }
  ],
