{"code":"import json, re
path = '/workspace/dumps/workspace/.overlong_tool_outputs/ayRWh89F2tyYpnYJcVuHHM.json'
with open(path,'r',encoding='utf-8') as f:
    data = json.load(f)
# Build rows: group by row, get columns A: name, B: carbs, C: protein, J: remarks
rows = {}
for cell in data['cells']:
    r = cell['row']
    rows.setdefault(r, {})[cell['column']] = cell

foods = {}
for r, cols in rows.items():
    name = cols.get(1, {}).get('value')
    carbs = cols.get(2, {}).get('value')
    protein = cols.get(3, {}).get('value')
    remark = cols.get(10, {}).get('value')
    if isinstance(name, str) and name.strip():
        foods[name.strip()] = {
            'carbs_per100': carbs,
            'protein_per100': protein,
            'remark': remark
        }

# Search by fuzzy contains
terms = ['鸡蛋','西红柿','番茄','桂鱼','鳜鱼','鲈鱼','凤梨','菠萝','瘦肉','猪肉','蒸鱼豉油','生抽','番茄酱','姜','小葱','葱','淀粉','香菇','蘑菇','蟹味菇','植物油','食用油','盐','白砂糖','香醋','鸡精','香葱','葱花']
found = {}
for t in terms:
    for k in foods:
        if t in k:
            found.setdefault(t, []).append((k, foods[k]))

print('Found terms:')
for t, lst in found.items():
    print(t, '->', [x[0] for x in lst])

# Also print a few likely candidates with '鱼'
fish = [k for k in foods.keys() if '鱼' in k]
print('Fish items sample:', fish[:50])
# And '蛋'
eggs = [k for k in foods.keys() if '蛋' in k]
print('Egg items:', eggs)
