{"query":"-- Determine manual task order and typical duration per group
WITH fe AS (
  SELECT TASK_NAME, MIN(CREATE_DATE) AS first_date, AVG(DATEDIFF('day', CREATE_DATE, DDL)) AS avg_days
  FROM LANDING_TASK_REMINDER.PUBLIC.GROUP_TASKS_FRONTEND
  GROUP BY TASK_NAME
), be AS (
  SELECT TASK_NAME, MIN(CREATE_DATE) AS first_date, AVG(DATEDIFF('day', CREATE_DATE, DDL)) AS avg_days
  FROM LANDING_TASK_REMINDER.PUBLIC.GROUP_TASKS_BACKEND
  GROUP BY TASK_NAME
), te AS (
  SELECT TASK_NAME, MIN(CREATE_DATE) AS first_date, AVG(DATEDIFF('day', CREATE_DATE, DDL)) AS avg_days
  FROM LANDING_TASK_REMINDER.PUBLIC.GROUP_TASKS_TESTING
  GROUP BY TASK_NAME
)
SELECT 'FRONTEND' AS grp, TASK_NAME, first_date, avg_days FROM fe ORDER BY first_date, TASK_NAME;
"}

{"query":"SELECT e.NAME AS SUP_NAME, e.EMAIL AS SUP_EMAIL
