tex_files = []
for dirpath, dirs, files in os.walk(root):
    for f in files:
        if f.endswith('.tex'):
            tex_files.append(os.path.join(dirpath, f))

label_pat = re.compile(r'\\label\{([^}]+)\}')
ref_pats = {
    'ref': re.compile(r'\\ref\{([^}]+)\}'),
    'autoref': re.compile(r'\\autoref\{([^}]+)\}'),
    'Sref': re.compile(r'\\S\\ref\{([^}]+)\}')
}

labels = set()
for path in tex_files:
    if os.path.basename(path) == 'math_commands.tex':
        continue
    txt = open(path, encoding='utf-8').read()
    labels |= set(m.group(1) for m in label_pat.finditer(txt))

missing = []
for path in tex_files:
    if os.path.basename(path) == 'math_commands.tex':
        continue
    txt = open(path, encoding='utf-8').read()
    for name,pat in ref_pats.items():
        for m in pat.finditer(txt):
            key = m.group(1)
            if key not in labels:
                missing.append((name, path, key))

print('Missing refs:', len(missing))
for m in missing:
    print(m)","filename":"scan_missing_refs.py","timeout":120}

