current_time = datetime.now().strftime("%Y-%m-%d %H:%M:%S")

for product in products_data:
    stock_quantity = product["stock_quantity"]
    threshold_str = get_meta_value(product["meta_data"], "stock_threshold")
    threshold = int(threshold_str) if threshold_str else 0
    supplier_info = get_meta_value(product["meta_data"], "supplier")
    category = get_meta_value(product["meta_data"], "category")

    print(f"Product: {product['name']}")
    print(f"  Stock: {stock_quantity}, Threshold: {threshold}")
    print(f"  Status: {'LOW STOCK' if stock_quantity < threshold else 'OK'}")
    print()

    if stock_quantity < threshold:
        # Calculate suggested order quantity (2x threshold - current stock, minimum 10)
        suggested_qty = max(10, (threshold * 2) - stock_quantity)

        low_stock_products.append({
            "product_id": product["id"],
            "product_name": product["name"],
            "sku": product["sku"],
            "current_stock": stock_quantity,
            "threshold": threshold,
            "supplier_name": supplier_info.get("name", "") if supplier_info else "",
            "supplier_id": supplier_info.get("supplier_id", "") if supplier_info else "",
            "supplier_contact": supplier_info.get("contact", "") if supplier_info else "",
            "alert_time": current_time,
            "suggested_order_quantity": suggested_qty,
            "category": category or ""
        })

print(f"\n=== LOW STOCK ANALYSIS RESULTS ===")
print(f"Total products analyzed: {len(products_data)}")
print(f"Products with low stock: {len(low_stock_products)}")
print("\nLow stock products:")
for product in low_stock_products:
    print(f"- {product['product_name']} (Stock: {product['current_stock']}, Threshold: {product['threshold']})")

# Prepare data for Google Sheets
sheets_data = []
for product in low_stock_products:
    sheets_data.append([
        str(product["product_id"]),
        product["product_name"],
        product["sku"],
        str(product["current_stock"]),
        str(product["threshold"]),
        product["supplier_name"],
        product["supplier_id"],
        product["supplier_contact"],
        product["alert_time"],
        str(product["suggested_order_quantity"])
    ])

print(f"\nData ready for Google Sheets: {len(sheets_data)} rows")
print("Low stock products data:", low_stock_products)"}

