{"code": "import json

# Pricing data collected from Apple's official education stores (where available)
pricing_data = {
    "United States": {
        "ipad_pro_256gb_11inch": 899,  # USD
        "apple_pencil_pro": 119,       # USD
        "currency": "USD",
        "total": 899 + 119
    },
    "Hong Kong": {
        "ipad_pro_256gb_11inch": 7199,  # HKD
        "apple_pencil_pro": 899,        # HKD
        "currency": "HKD",
        "total": 7199 + 899
    },
    "Singapore": {
        "ipad_pro_256gb_11inch": 1349,  # SGD
        "apple_pencil_pro": 179,        # SGD
        "currency": "SGD",
        "total": 1349 + 179
    },
    "Mainland China": {
        "ipad_pro_256gb_11inch": 8999,  # RMB (no education discount available)
        "apple_pencil_pro": 999,        # RMB
        "currency": "RMB",
        "total": 8999 + 999
    }
}

# Current exchange rates (approximate, as of late 2024)
exchange_rates_to_usd = {
    "USD": 1.0,
    "HKD": 0.128,  # 1 HKD = 0.128 USD
    "SGD": 0.74,   # 1 SGD = 0.74 USD
    "RMB": 0.138   # 1 RMB = 0.138 USD
}

# Convert all prices to USD for comparison
usd_prices = {}
for region, data in pricing_data.items():
    currency = data["currency"]
    rate = exchange_rates_to_usd[currency]
    usd_total = data["total"] * rate
    usd_prices[region] = {
        "total_local": data["total"],
        "currency": currency,
        "total_usd": round(usd_total, 2)
    }

# Find the cheapest option
cheapest_region = min(usd_prices.keys(), key=lambda x: usd_prices[x]["total_usd"])
cheapest_price = usd_prices[cheapest_region]["total_usd"]

print("Price Comparison (converted to USD):")
print("="*50)
for region, data in usd_prices.items():
    print(f"{region}: {data['currency']} {data['total_local']} = USD {data['total_usd']}")

print(f"\nCheapest option: {cheapest_region} at USD {cheapest_price}")

# Create the result JSON
result = {
    "cheapest_region": cheapest_region,
    "cheapest_total_price": cheapest_price
}

print(f"\nResult JSON:")
print(json.dumps(result, indent=2))"}
