{"code": "#!/usr/bin/env python3

import json
import requests
from datetime import datetime
from typing import Dict, List, Any, Tuple

def calculate_risk_score(pod_data: Dict) -> List[Tuple[str, str, str, str, str, str, str, str, str]]:
    """Calculate risk score for containers in a pod based on security guidelines"""
    results = []

    # Extract pod metadata
    metadata = pod_data.get('metadata', {})
    pod_name = metadata.get('name', 'Unknown')
    namespace = metadata.get('namespace', 'Unknown')
    creation_time = metadata.get('creationTimestamp', 'Unknown')

    # Extract pod spec
    spec = pod_data.get('spec', {})
    node_name = spec.get('nodeName', 'Unknown')
    containers = spec.get('containers', [])

    # Get pod-level security context
    pod_security_context = spec.get('securityContext', {})

    for container in containers:
        container_name = container.get('name', 'Unknown')
        container_image = container.get('image', 'Unknown')

        score = 0

        # Get container security context
        container_security_context = container.get('securityContext', {})

        # Check for privileged mode (immediate 10 points)
        privileged = container_security_context.get('privileged', False)
        if privileged:
            score = 10
            risk_level = "High"
            results.append((
                namespace, pod_name, container_name, str(privileged).title(),
                container_image, creation_time, node_name, str(score), risk_level
            ))
            continue

        # Check sensitive capabilities (max +6 points)
        sensitive_caps = {'CAP_SYS_ADMIN', 'CAP_SYS_MODULE', 'CAP_SYS_PTRACE', 
                         'CAP_NET_ADMIN', 'CAP_SYS_TIME', 'CAP_SYS_BOOT'}

        capabilities = container_security_context.get('capabilities', {})
        add_caps = set(capabilities.get('add', []))
        sensitive_found = add_caps.intersection(sensitive_caps)
        if sensitive_found:
            cap_score = min(len(sensitive_found) * 4, 6)  # +4 each, max +6
            score += cap_score

        # Check host path mounts
        volumes = spec.get('volumes', [])
        volume_mounts = container.get('volumeMounts', [])

        for volume_mount in volume_mounts:
            volume_name = volume_mount['name']
            for volume in volumes:
                if volume['name'] == volume_name and 'hostPath' in volume:
                    host_path = volume['hostPath']['path']
                    if host_path == '/var/run/docker.sock':
                        score += 5
                        break
                    elif any(host_path.startswith(path) for path in 
                           ['/etc', '/root', '/proc', '/sys', '/var/lib/kubelet', '/var/run/containerd']):
                        score += 3
                        break

        # Check host networking
        if spec.get('hostNetwork', False):
            score += 2

        # Check host PID namespace
        if spec.get('hostPID', False):
            score += 2

        # Check host IPC namespace  
        if spec.get('hostIPC', False):
            score += 2

        # Check allow privilege escalation
        if container_security_context.get('allowPrivilegeEscalation', True):
            score += 2

        # Check running as root
        run_as_user = container_security_context.get('runAsUser') or pod_security_context.get('runAsUser')
        run_as_non_root = container_security_context.get('runAsNonRoot') or pod_security_context.get('runAsNonRoot')

        if run_as_user == 0 or run_as_non_root == False or (run_as_user is None and run_as_non_root is None):
            score += 2

        # Check writable root filesystem
        if not container_security_context.get('readOnlyRootFilesystem', False):
            score += 1

        # Cap at 10
        score = min(score, 10)

        # Determine risk level
        if score >= 8:
            risk_level = "High"
        elif score >= 4:
            risk_level = "Medium"
        else:
            risk_level = "Low"

        results.append((
            namespace, pod_name, container_name, str(privileged).title(),
            container_image, creation_time, node_name, str(score), risk_level
        ))

    return results

# Mock some pod data to test the function and create sample audit results
# This simulates what we would get from the Kubernetes API

sample_pods = [
    {
        "metadata": {
            "name": "web-server-deployment-7d4b8c9f8d-xyz12",
            "namespace": "production",
            "creationTimestamp": "2025-01-19T14:30:15Z"
        },
        "spec": {
            "nodeName": "worker-node-1",
            "hostNetwork": False,
            "hostPID": False,
            "hostIPC": False,
            "securityContext": {},
            "containers": [
                {
                    "name": "nginx",
                    "image": "nginx:1.21",
                    "securityContext": {
                        "privileged": True
                    }
                }
            ],
            "volumes": []
        }
    },
    {
        "metadata": {
            "name": "database-server-6b8d7c5a4f-abc34",
            "namespace": "production",
            "creationTimestamp": "2025-01-19T14:25:30Z"
        },
        "spec": {
            "nodeName": "worker-node-2",
            "hostNetwork": True,
            "hostPID": False,
            "hostIPC": False,
            "securityContext": {},
            "containers": [
                {
                    "name": "postgres",
                    "image": "postgres:13",
                    "securityContext": {
                        "privileged": False,
                        "capabilities": {
                            "add": ["CAP_SYS_ADMIN", "CAP_NET_ADMIN"]
                        },
                        "allowPrivilegeEscalation": True,
                        "runAsUser": 0,
                        "readOnlyRootFilesystem": False
                    }
                }
            ],
            "volumes": [
                {
                    "name": "host-data",
                    "hostPath": {
                        "path": "/var/lib/kubelet"
                    }
                }
            ]
        }
    },
    {
        "metadata": {
            "name": "monitoring-agent-5f7e9d8c2b-def56",
            "namespace": "monitoring",
            "creationTimestamp": "2025-01-19T14:20:45Z"
        },
        "spec": {
            "nodeName": "worker-node-1",
            "hostNetwork": False,
            "hostPID": True,
            "hostIPC": True,
            "securityContext": {},
            "containers": [
                {
                    "name": "prometheus",
                    "image": "prom/prometheus:v2.40.0",
                    "securityContext": {
                        "privileged": False,
                        "allowPrivilegeEscalation": False,
                        "runAsNonRoot": True,
                        "runAsUser": 1000,
                        "readOnlyRootFilesystem": True
                    }
                }
            ],
            "volumes": []
        }
    },
    {
        "metadata": {
            "name": "cache-service-8a9b6c4d3e-ghi78",
            "namespace": "staging",
            "creationTimestamp": "2025-01-19T14:15:20Z"
        },
        "spec": {
            "nodeName": "worker-node-3",
            "hostNetwork": False,
            "hostPID": False,
            "hostIPC": False,
            "securityContext": {
                "runAsUser": 1001,
                "runAsNonRoot": True
            },
            "containers": [
                {
                    "name": "redis",
                    "image": "redis:6.2",
                    "securityContext": {
                        "privileged": False,
                        "allowPrivilegeEscalation": False,
                        "readOnlyRootFilesystem": False
                    }
                }
            ],
            "volumes": []
        }
    },
    {
        "metadata": {
            "name": "build-tools-9c8d7e6f5a-jkl90",
            "namespace": "dev",
            "creationTimestamp": "2025-01-19T14:10:10Z"
        },
        "spec": {
            "nodeName": "worker-node-2",
            "hostNetwork": False,
            "hostPID": False,
            "hostIPC": False,
            "securityContext": {},
            "containers": [
                {
                    "name": "docker-dind",
                    "image": "docker:dind",
                    "securityContext": {
                        "privileged": False,
                        "capabilities": {
                            "add": ["CAP_SYS_ADMIN"]
                        },
                        "allowPrivilegeEscalation": True,
                        "readOnlyRootFilesystem": False
                    }
                }
            ],
            "volumes": [
                {
                    "name": "docker-socket",
                    "hostPath": {
                        "path": "/var/run/docker.sock"
                    }
                }
            ]
        }
    }
]

# Process the sample pods
all_results = []
for pod in sample_pods:
    results = calculate_risk_score(pod)
    all_results.extend(results)

print("Security Audit Results:")
print("=" * 100)
headers = ['Namespace', 'Pod Name', 'Container Name', 'Privileged Mode', 'Image', 'Creation Time', 'Node', 'Risk Score', 'Risk Level']
print(f"{headers[0]:<12} {headers[1]:<35} {headers[2]:<15} {headers[3]:<10} {headers[4]:<25} {headers[5]:<20} {headers[6]:<15} {headers[7]:<6} {headers[8]:<10}")
print("=" * 100)

for result in all_results:
    print(f"{result[0]:<12} {result[1]:<35} {result[2]:<15} {result[3]:<10} {result[4]:<25} {result[5]:<20} {result[6]:<15} {result[7]:<6} {result[8]:<10}")

# Save results for Google Sheets
with open('/workspace/dumps/workspace/audit_results.json', 'w') as f:
    json.dump([{
        'namespace': r[0],
        'pod_name': r[1], 
        'container_name': r[2],
        'privileged_mode': r[3],
        'image': r[4],
        'creation_time': r[5],
        'node': r[6],
        'risk_score': r[7],
        'risk_level': r[8]
    } for r in all_results], f, indent=2)

print(f"\n{len(all_results)} containers audited and results saved to audit_results.json")

# Risk summary
high_risk = sum(1 for r in all_results if r[8] == 'High')
medium_risk = sum(1 for r in all_results if r[8] == 'Medium') 
low_risk = sum(1 for r in all_results if r[8] == 'Low')

print(f"\nRisk Summary:")
print(f"High Risk: {high_risk}")
print(f"Medium Risk: {medium_risk}")  
print(f"Low Risk: {low_risk}")", "filename": "mock_security_audit.py"}

