import json
from datetime import datetime
from typing import Dict, List, Any, Tuple

def calculate_risk_score(pod_data: Dict) -> List[Tuple[str, str, str, str, str, str, str, str, str]]:
    """Calculate risk score for containers in a pod based on security guidelines"""
    results = []

    # Extract pod metadata
    metadata = pod_data.get('metadata', {})
    pod_name = metadata.get('name', 'Unknown')
    namespace = metadata.get('namespace', 'Unknown')
    creation_time = metadata.get('creationTimestamp', 'Unknown')

    # Extract pod spec
    spec = pod_data.get('spec', {})
    node_name = spec.get('nodeName', 'Unknown')
    containers = spec.get('containers', [])

    # Get pod-level security context
    pod_security_context = spec.get('securityContext', {})

    for container in containers:
        container_name = container.get('name', 'Unknown')
        container_image = container.get('image', 'Unknown')

        score = 0

        # Get container security context
        container_security_context = container.get('securityContext', {})

        # Check for privileged mode (immediate 10 points)
        privileged = container_security_context.get('privileged', False)
        if privileged:
            score = 10
            risk_level = "High"
            results.append((
                namespace, pod_name, container_name, str(privileged).title(),
                container_image, creation_time, node_name, str(score), risk_level
            ))
            continue

        # Check sensitive capabilities (max +6 points)
        sensitive_caps = {'CAP_SYS_ADMIN', 'CAP_SYS_MODULE', 'CAP_SYS_PTRACE', 
                         'CAP_NET_ADMIN', 'CAP_SYS_TIME', 'CAP_SYS_BOOT', 'SYS_ADMIN'}

        capabilities = container_security_context.get('capabilities', {})
        add_caps = set(capabilities.get('add', []))
        sensitive_found = add_caps.intersection(sensitive_caps)
        if sensitive_found:
            cap_score = min(len(sensitive_found) * 4, 6)  # +4 each, max +6
            score += cap_score

        # Check host path mounts
        volumes = spec.get('volumes', [])
        volume_mounts = container.get('volumeMounts', [])

        for volume_mount in volume_mounts:
            volume_name = volume_mount['name']
            for volume in volumes:
                if volume['name'] == volume_name and 'hostPath' in volume:
                    host_path = volume['hostPath']['path']
                    if host_path == '/var/run/docker.sock':
                        score += 5
                        break
                    elif any(host_path.startswith(path) for path in 
                           ['/etc', '/root', '/proc', '/sys', '/var/lib/kubelet', '/var/run/containerd']):
                        score += 3
                        break

        # Check host networking
        if spec.get('hostNetwork', False):
            score += 2

        # Check host PID namespace
        if spec.get('hostPID', False):
            score += 2

        # Check host IPC namespace  
        if spec.get('hostIPC', False):
            score += 2

        # Check allow privilege escalation
        if container_security_context.get('allowPrivilegeEscalation', True):
            score += 2

        # Check running as root
        run_as_user = container_security_context.get('runAsUser') or pod_security_context.get('runAsUser')
        run_as_non_root = container_security_context.get('runAsNonRoot') or pod_security_context.get('runAsNonRoot')

        if run_as_user == 0 or run_as_non_root == False or (run_as_user is None and run_as_non_root is None):
            score += 2

        # Check writable root filesystem
        if not container_security_context.get('readOnlyRootFilesystem', False):
            score += 1

        # Cap at 10
        score = min(score, 10)

        # Determine risk level
        if score >= 8:
            risk_level = "High"
        elif score >= 4:
            risk_level = "Medium"
        else:
            risk_level = "Low"

        results.append((
            namespace, pod_name, container_name, str(privileged).title(),
            container_image, creation_time, node_name, str(score), risk_level
        ))

    return results

