{"code": "import json

def calculate_risk_score(container_spec, pod_spec):
    """Calculate risk score based on security guidelines"""
    score = 0

    # Check for privileged mode
    security_context = container_spec.get('securityContext', {})
    if security_context.get('privileged', False):
        return 10  # Immediate high risk

    # Check for sensitive capabilities
    capabilities = security_context.get('capabilities', {})
    add_caps = capabilities.get('add', [])
    sensitive_caps = ['CAP_SYS_ADMIN', 'SYS_ADMIN', 'CAP_SYS_MODULE', 'SYS_MODULE', 
                     'CAP_SYS_PTRACE', 'SYS_PTRACE', 'CAP_NET_ADMIN', 'NET_ADMIN', 
                     'CAP_SYS_TIME', 'SYS_TIME', 'CAP_SYS_BOOT', 'SYS_BOOT']

    sensitive_count = sum(1 for cap in add_caps if cap in sensitive_caps)
    score += min(sensitive_count * 4, 6)  # Max +6 points

    # Check for host path mounts
    volumes = pod_spec.get('volumes', [])
    volume_mounts = container_spec.get('volumeMounts', [])

    for volume in volumes:
        if 'hostPath' in volume:
            host_path = volume['hostPath']['path']
            # Check if this volume is mounted by our container
            volume_name = volume['name']
            if any(vm['name'] == volume_name for vm in volume_mounts):
                if host_path == '/var/run/docker.sock':
                    score += 5
                    break  # Don't stack with +3
                elif host_path.startswith(('/etc', '/root', '/proc', '/sys', '/var/lib/kubelet', '/var/run/containerd')):
                    score += 3
                    break  # Don't stack multiple host paths

    # Check for host network
    if pod_spec.get('hostNetwork', False):
        score += 2

    # Check for host PID namespace
    if pod_spec.get('hostPID', False):
        score += 2

    # Check for host IPC namespace
    if pod_spec.get('hostIPC', False):
        score += 2

    # Check for allow privilege escalation
    if security_context.get('allowPrivilegeEscalation', True):  # Default is true if not specified
        score += 2

    # Check for running as root
    run_as_user = security_context.get('runAsUser')
    run_as_non_root = security_context.get('runAsNonRoot')

    if run_as_user == 0 or run_as_non_root == False or (run_as_user is None and run_as_non_root is None):
        score += 2

    # Check for writable root filesystem
    if not security_context.get('readOnlyRootFilesystem', False):  # Default is false
        score += 1

    return min(score, 10)  # Cap at 10

def get_risk_level(score):
    """Convert score to risk level"""
    if score >= 8:
        return "High"
    elif score >= 4:
        return "Medium"
    else:
        return "Low"

def analyze_pod_data(pod_json_text):
    """Analyze a single pod's JSON data"""
    pod_data = json.loads(pod_json_text)

    namespace = pod_data['metadata']['namespace']
    pod_name = pod_data['metadata']['name']
    creation_time = pod_data['metadata']['creationTimestamp']
    node_name = pod_data['spec'].get('nodeName', 'Unknown')

    results = []

    # Get containers in the pod
    containers = pod_data['spec'].get('containers', [])

    for container in containers:
        container_name = container['name']
        image = container['image']

        # Calculate risk score
        risk_score = calculate_risk_score(container, pod_data['spec'])
        risk_level = get_risk_level(risk_score)

        # Check privileged mode for display
        security_context = container.get('securityContext', {})
        is_privileged = security_context.get('privileged', False)

        results.append({
            'namespace': namespace,
            'pod_name': pod_name,
            'container_name': container_name,
            'privileged_mode': str(is_privileged),
            'image': image,
            'creation_time': creation_time,
            'node': node_name,
            'risk_score': str(risk_score),
            'risk_level': risk_level
        })

    return results

