        "nodeName": "cluster-safety-audit-control-plane"
    }
}
    '''
]

# Analyze all pods
all_results = []
for pod_json in pod_jsons:
    results = analyze_pod_data(pod_json)
    all_results.extend(results)

# Add some additional sample pods to have a more comprehensive audit
additional_pods = [
    {
        'namespace': 'kube-system',
