function.arguments:
{"query": "query ProjectInfo($entity: String!, $project: String!) {
    project(name: $project, entityName: $entity) {
        id
        name
        entityName
        description
        runCount
    }
}", "variables": {"entity":"mluo","project":"deepscaler-1.5b"}}

{"query": "query GetRuns($entity: String!, $project: String!) {
    project(name: $project, entityName: $entity) {
        runs(first: 10) {
            edges {
                node {
                    id
                    name
                    displayName
                    state
                    createdAt
                    summaryMetrics
                    historyKeys
                }
            }
            pageInfo {
                endCursor
                hasNextPage
            }
        }
    }
}", "variables": {"entity":"mluo","project":"deepscaler-1.5b"}}

